        public NegozioAbiti(Vector3 blip, int blipId, int blipColor, Vector4 vestiti, Vector4 scarpe, Vector4 maglie, Vector4 pantaloni, Vector4 occhiali, Vector4 accessori)
        {
            Blip = blip;
            BlipId = blipId;
